import { Box, Container, Card, Typography, CardMedia, Row, Grid} from '@mui/material'
import React from 'react'

export const User = (props) => {
    let displayName = props.data.displayName
    let profileImage = props.data.profileImage ? props.data.profileImage : "https://imgur.com/gallery/AizUHEf"
    return(
        <Grid container>
            <Card style={{margin:3}}>
                <img style={{borderRadius:"50%", width: "50%", height: "50%"}} src={profileImage} />
                {/* <CardMedia
                    component="img"
                    alt="green iguana"
                    height="140"
                    image={profileImage}
                /> */}
            
                <Typography variant="h4">{displayName}</Typography>
            </Card>
        </Grid>
    )
}

export const FriendsList = () => {
    var data = {
        type: "authors",      
        items:[
            {
                type:"author",
                id:"http://127.0.0.1:5454/authors/1d698d25ff008f7538453c120f581471",
                url:"http://127.0.0.1:5454/authors/1d698d25ff008f7538453c120f581471",
                host:"http://127.0.0.1:5454/",
                displayName:"Greg Johnson",
                github: "http://github.com/gjohnson",
                profileImage: "https://i.imgur.com/k7XVwpB.jpeg"
            },
            {
                type:"author",
                id:"http://127.0.0.1:5454/authors/9de17f29c12e8f97bcbbd34cc908f1baba40658e",
                host:"http://127.0.0.1:5454/",
                displayName:"Lara Croft",
                url:"http://127.0.0.1:5454/authors/9de17f29c12e8f97bcbbd34cc908f1baba40658e",
                github: "http://github.com/laracroft",
                profileImage: "https://i.imgur.com/k7XVwpB.jpeg"
            }
        ]
    }
    let users = data.items
    // console.log(users)
    return (
        < >
            {users.map((d) => {
                // return (<Card style={{margin:20}}>{d.displayName}</Card>)
                return(<User data={d}></User>)
            })}
        </>

    )
}
